import React, { useState } from "react";
import {
  Tabs,
  Tab,
  TextField,
  Button,
  Typography,
  Box,
  Paper,
} from "@mui/material";
import "./styles.css";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box className="tab-panel-box">{children}</Box>}
    </div>
  );
}

function LoginRegister({ setLoggedInUser }) {
  const [tabValue, setTabValue] = useState(0);

  const handleChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Hàm này chỉ để giả lập việc đăng nhập thành công
  const handleLogin = (e) => {
    e.preventDefault();
    // Trong một ứng dụng thực tế, bạn sẽ gọi API ở đây
    // và nếu thành công, gọi setLoggedInUser(userData)
    console.log("Login logic goes here");
  };

  return (
    <Paper elevation={3} className="login-register-paper">
      <Tabs value={tabValue} onChange={handleChange} variant="fullWidth">
        <Tab label="Login" />
        <Tab label="Register" />
      </Tabs>
      <TabPanel value={tabValue} index={0}>
        <Box
          component="form"
          onSubmit={handleLogin}
          className="login-form"
          noValidate
          autoComplete="off"
        >
          <Typography variant="h5" component="h1" gutterBottom>
            Login
          </Typography>
          <TextField label="Username" variant="outlined" fullWidth />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            fullWidth
          />
          <Button type="submit" variant="contained" color="primary" className="form-button">
            Login
          </Button>
        </Box>
      </TabPanel>
      <TabPanel value={tabValue} index={1}>
        <Box
          component="form"
          className="register-form"
          noValidate
          autoComplete="off"
        >
          <Typography variant="h5" component="h1" gutterBottom align="center">
            Register
          </Typography>
          <TextField label="Username" variant="outlined" fullWidth />
          <TextField label="Password" type="password" variant="outlined" fullWidth />
          <TextField label="First Name" variant="outlined" fullWidth />
          <TextField label="Last Name" variant="outlined" fullWidth />
          <Button variant="contained" color="primary" className="form-button">
            Register
          </Button>
        </Box>
      </TabPanel>
    </Paper>
  );
}

export default LoginRegister;
