import React, { useEffect, useState } from "react";
import { Card, CardContent, Typography, Button, TextField, Box, Alert } from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import fetchModel, { putModel } from "../../lib/fetchModelData";

function UserEdit({ loggedInUser }) {
  const userId = useParams();
  const navigate = useNavigate();
  const [userData,setUserData] = useState({
    last_name: "",
    location: "",
    description: "",
    occupation: "",
  });
  const [loading,setLoading] = useState(true);
  const [error,setError] = useState(null);
  
  useEffect(() =>{
    const getUser = async () =>{
        try{
            const response = await fetchModel(`/user/${userId}`);
            const user = response.data;
            setUserData({
                last_name: user.last_name,
                location: user.location,
                description: user.description,
                occupation: user.occupation,
            });
        } catch (err){
            console.error("Error fetching user:", err);
            setError("Could not load user data.");
        } finally{
            setLoading(false);
        }
    };
    getUser();
  }, [userId]);
}
export default UserEdit;
