import React, { useEffect, useState } from "react";
import { Card, CardContent, Typography, Button, TextField, Box, Alert } from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import fetchModel, { putModel } from "../../lib/fetchModelData";

function UserEdit({ loggedInUser }) {
  const { userId } = useParams();
  const navigate = useNavigate();

  const [userData, setUserData] = useState({
    last_name: "",
    location: "",
    description: "",
    occupation: "",
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    const getUser = async () => {
      try {
        const response = await fetchModel(`/user/${userId}`);
        const user = response.data;
        if (user) {
          setUserData({
            last_name: user.last_name || "",
            location: user.location || "",
            description: user.description || "",
            occupation: user.occupation || "",
          });
        }
        setLoading(false);
      } catch (err) {
        console.error("Error fetching user:", err);
        setError("Could not load user data.");
        setLoading(false);
      }
    };

    getUser();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);

    try {
      await putModel(`/user/${userId}`, userData);
      setSuccess(true);
      setTimeout(() => {
        navigate(`/users/${userId}`);
      }, 1000);
    } catch (err) {
      console.error("Update failed:", err);
      setError(err.message || "Update failed.");
    }
  };

  if (loading) return <Typography>Loading...</Typography>;

  if (loggedInUser && loggedInUser._id !== userId) {
    return <Typography variant="h6" color="error">You are not authorized to edit this profile.</Typography>;
  }

  return (
    <Card>
      <CardContent>
        <Typography variant="h5" gutterBottom>Edit Profile</Typography>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2 }}>Update successful! Redirecting...</Alert>}
        <Box component="form" onSubmit={handleSubmit} noValidate>
          <TextField
            label="Name" name="last_name" fullWidth margin="normal"
            value={userData.last_name} onChange={handleChange} required
          />
          <TextField
            label="Location" name="location" fullWidth margin="normal"
            value={userData.location} onChange={handleChange}
          />
          <TextField
            label="Description" name="description" fullWidth margin="normal"
            multiline rows={4}
            value={userData.description} onChange={handleChange}
          />
          <TextField
            label="Occupation" name="occupation" fullWidth margin="normal"
            value={userData.occupation} onChange={handleChange}
          />
          <Box sx={{ mt: 2 }}>
            <Button type="submit" variant="contained" color="primary" sx={{ mr: 2 }}>Save</Button>
            <Button variant="outlined" onClick={() => navigate(`/users/${userId}`)}>Cancel</Button>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
}
export default UserEdit;
